public static function my_static_array() {
	$t_my_static_array = array();
	foreach(self::$my_static_array as $k => $v) {
		$t_my_static_array[$k] = _t("MyTestClass.my_static_array_{$key}", $v);
	}
	return $t_my_static_array;
}
